		<tr><td colspan="2">FACTURA $Comprobante.folio</td><td colspan="2">$Comprobante.LugarExpedicion</td><td colspan="2">$Comprobante.fecha</td></tr>
		<tr class="centrado"><td colspan="3">$Comprobante.formaDePago</td><td>METODO DE PAGO: $Comprobante.metodoDePago</td><td colspan="2">$Comprobante.cuenta</td></tr>
