local codes = {['&'] = '&amp;', ['&amp;'] = '&',
	['<'] = '&lt;', ['&lt;'] = '<',
	['>'] = '&gt;', ['&gt;'] = '>',
	['"'] = '&quot;' , ['&quot;'] = '"',
	["'"] = '&apos;' , ['&apos;'] = "'"}

local comprobante = {
	['xsi:schemaLocation']="http://www.sat.gob.mx/cfd/3 http://www.sat.gob.mx/sitio_internet/cfd/3/cfdv32.xsd",
	['xmlns:cfdi']="http://www.sat.gob.mx/cfd/3",
	['xmlns:tfd']="http://www.sat.gob.mx/TimbreFiscalDigital",
	['xmlns:xsi']="http://www.w3.org/2001/XMLSchema-instance",
	['xmlns:xs']="http://www.w3.org/2001/XMLSchema",
	version="3.2",
}

local header = { 'Concepto.noIdentificacion', 'Concepto.cantidad', 'Concepto.unidad', 'Concepto.descripcion', 'Concepto.valorUnitario', 'Concepto.importe',
		clave = 'Concepto.noIdentificacion', desc = 'Concepto.descripcion' }

local theader = '<tr><th>Clave</th><th>Cantidad</th><th>Unidad</th><th>Descripcion</th><th>PrecioUnitario</th><th>Importe</th></tr>'

local function escape(s) return s:gsub('[&<>\'"]', codes) end

local function safeguard(a)
    local desc, clave = header.desc, header.clave
    a[desc] = escape(a[desc])
    local k = a[clave]
    a[clave] = (k == '0' and '' or k)
    return a
end

local function tdata(s) return string.format('<td>%s</td>', s) end

local function likerow(a) return table.concat(fd.reduce(header, fd.map(function(k) return tdata(a[k]) end), fd.into, {}), '') end

local function asrow(s) return string.format('<tr class="centrado">%s</tr>', s) end

local function conceptos(w)
    local cptos = w.Conceptos
    local ret = fd.reduce(cptos, fd.map(safeguard), fd.map(likerow), fd.map(asrow), fd.into, {theader})
    w.Conceptos = table.concat(ret, '\n\t\t')
    return w
end

local function traslados(w)
    w['Comprobante.letra'] = letra(w['Comprobante.total'])
    w.Traslados = string.format('<tr><td colspan="5" class="derecha">%s(%s%%)</td><td class="derecha">%s</td></tr>', w['Traslado.impuesto'], w['Traslado.tasa'], w['Traslado.importe'])
    return w
end

local function addlogo(w) w.LOGO = logo; return w end

local ret = fd.comp{addlogo, conceptos, traslados, tim.obtener'53C1BA08-298D-4323-992C-017716926180'}
local ans = html:gsub('$([%a.]+)', ret):gsub('$[%a.]+', '')

print(ans)


